require 'aws-sdk-sagemakerruntime'
require 'json'

# Support S3 link
input_data = {
  "input" => ''
}
# Endpoint name
endpoint_name = 'Endpoint-Census-Intake-Sample'
# Region name
region_name = 'us-east-1'

# Create a SageMaker Runtime Service client
client = Aws::SageMakerRuntime::Client.new(region: region_name)
puts 'client created'

begin
  response = client.invoke_endpoint({
    endpoint_name: endpoint_name,
    content_type: 'application/json',
    body: input_data.to_json # Serialize your input data
  })

  # Deserialize the response
  response_body = response.body.read
  response_json = JSON.parse(response_body)

  # Get predictions
  puts response_json
rescue Aws::SageMakerRuntime::Errors::ServiceError => error
  puts "Service error occurred: #{error.message}"
end
